INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (408, 'বিপ্রদাশ বড়ুয়া', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'Bipradash Barua- জন্ম ২ আশ্বিন ১৮৬২ শকাব্দ (২০ সেপ্টেম্বর ১৯৪০) চট্টগ্রামের ইছামতী গ্রামে। পড়াশুনা চট্টগ্রাম, রাঙামাটি ও ঢাকা বিশ্ববিদ্যালয়ে। গল্প, উপন্যাস, প্রকৃতি ও বিচিত্র বিষয়ে লেখেন। পাখি, সমুদ্র ও বৃক্ষ, বনমর্মরভূমি, ভ্রমণ, গবেষণা-বিশাল লেখার ভুবন। বাংলাদেশের আনাচ-কানাচ উঠে আসে দৈনিক পত্রিকার কলামে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (410, 'আনিসুজ্জামান', '123456', 'https://ds.rokomari.store/rokomari110/people/1fa95e2eca94_1009.jpg', 'আনিসুজ্জামান একাধারে একজন বরেণ্য শিক্ষাবিদ, লেখক, গবেষক, ভাষা সংগ্রামী, সংবিধানের অনুবাদক এবং ইমেরিটাস অধ্যাপক। এককথায় তিনি একজন জীবন্ত কিংবদন্তি। এই গুণীজন ১৯৩৭ সালের ১৮ই ফেব্রুয়ারি ভারতের পশ্চিমবঙ্গের চব্বিশ পরগনা জেলার বসিরহাটে এক উচ্চশিক্ষিত পরিবারে জন্মগ্রহণ করেন। তার বর্ণাঢ্য শিক্ষাজীবন শুরু হয় কলকাতার পার্ক সার্কাস হাই স্কুল থেকে। কৈশোরে পরিবারসমেত বাংলাদেশে চলে আসলে খুলনা শহরের এক স্কুলে ভর্তি হন। ১৯৫১ সালে ঢাকার প্রিয়নাথ হাই স্কুল থেকে মাধ্যমিক এবং ১৯৫৩ সালে জগন্নাথ কলেজ থেকে উচ্চ মাধ্যমিক পাস করেন আনিসুজ্জামান। এরপর তিনি ঢাকা বিশ্ববিদ্যালয়ের বাংলা বিভাগে ভর্তি হন। সেখানে প্রথম শ্রেণীতে প্রথম স্থান অর্জন করে স্নাতকোত্তর সম্পন্ন করেন।');
