INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (3852, 'জুলফিকার নিউটন', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', '১৯৬৪ সালের ১৪ এপ্রিল দেবিদ্বার উপজেলার বারেরা গ্রামের বনেদী কাজি পরিবারে জন্মগ্রহণ করেন। তিনি স্কুল, কলেজ এবং বিশ^বিদ্যালয়ে কৃতিমান ছাত্র ছিলেন এবং প্রথম শ্রেণীর সাথে অনার্সসহ এম.এ. ডিগ্রি এবং পরবর্তীতে বিশ^ভারতী বিশ^বিদ্যালয় থেকে রবীন্দ্রনাথের জীবনদর্শনের ওপর উচ্চতর গবেষণা সম্পন্ন করেন। চট্টগ্রাম বিশ্ববিদ্যালয় কেন্দ্রীয় ছাত্র সংসদ (চাকসুর) সাবেক সাহিত্য সম্পাদক, সিনেট সদস্য, নন্দন পত্রিকার সম্পাদক এবং পরবর্তীতে দেশ বিদেশের একাধিক বিশ্ববিদ্যালয়ে অধ্যাপনা, আমন্ত্রিত বক্তা, গবেষণা ও বিশেষজ্ঞতার ভূমিকায় সংযুক্ত ছিলেন। গল্প, উপন্যাস, অনুবাদ এবং প্রবন্ধ-গবেষণা, সাহিত্যতত্ত্ব, দর্শন, সংগীত, সংস্কৃতি, রাজনীতি, সমকালীন ঘটনার ঘাত-প্রতিঘাত প্রতিক্ষেত্রেই জুলফিকার নিউটনের সংবিৎ সক্রিয় ও সুপ্রকাশ। তাঁর গল্প-উপন্যাস যেমন প্রীতিপদ, অনুবাদ সাহিত্য যেমন সুখপ্রদ, প্রবন্ধ ও গবেষণা তেমনই কোন না কোন দিক থেকে চমকপ্রদ।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (3853, 'মুহম্মদ জাফর ইকবাল', '123456', 'https://ds.rokomari.store/rokomari110/people/1323bb4c3d84_93.jpg', 'বাংলাদেশের কিশোর-কিশোরী পাঠকদের কাছে সবচেয়ে জনপ্রিয় নাম মুহম্মদ জাফর ইকবাল। তিনি মূলত এ দেশের একজন বিখ্যাত লেখক, পদার্থবিদ এবং শিক্ষাবিদ। কিশোর সাহিত্য, শিশুতোষ গ্রন্থ, বৈজ্ঞানিক কল্পকাহিনী, গণিত বিষয়ক বই এর জন্য খুব অল্প সময়েই জনপ্রিয়তা লাভ করেন তিনি। মুহম্মদ জাফর ইকবাল ১৯৫২ সালের ২৩ ডিসেম্বর সিলেটে জন্মগ্রহণ করেন। মুক্তিযোদ্ধা বাবা ফয়জুর রহমানের চাকরির সুবাদে দেশের বিভিন্ন জেলাতেই তিনি পড়াশোনা করেছেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (3855, 'সেলিনা হোসেন', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', '২১টি উপন্যাস, ৭টি গল্পগ্রন্থ ও ৪টি প্রবন্ধগ্রন্থের রচয়িতা সেলিনা হোসেন বাংলাদেশের একজন জনপ্রিয় কথাসাহিত্যিক। সমকালীন রাজনৈতিক সংকট ও দ্বন্দ্বের উৎস ও প্রেক্ষাপট উঠে এসেছে সেলিনা হোসেন এর বই সমূহ-তে। সেলিনা হোসেন এর বই সমগ্র অনূদিত হয়েছে ইংরেজি, রুশসহ একাধিক ভাষায়। প্রবীণ এ লেখিকা ২০১৪ সাল থেকে বাংলাদেশ শিশু একাডেমির চেয়ারম্যান হিসেবে দায়িত্ব পালন করার পর কর্মজীবন থেকে অবসর নেন। সেলিনা হোসেন ১৯৪৭ সালের ১৪ই জুন রাজশাহীতে জন্মগ্রহণ করেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (3860, 'ইমদাদুল হক মিলন', '123456', 'https://ds.rokomari.store/rokomari110/people/ed4943e549d4_25.jpg', '১৯৫৫ সালের ৮ সেপ্টেম্বর বিক্রমপুরের মেদিনীমণ্ডল গ্রামে প্রখ্যাত কথাসাহিত্যিক ইমদাদুল হক মিলনের জন্ম। লেখনীশক্তির পাশাপাশি তার রয়েছে নাট্যরচনায় পারদর্শিতা। বর্তমানে বাংলাদেশের মূলধারার সংবাদপত্র ‘কালের কন্ঠ’-এর সম্পাদক পদেও নিয়োজিত রয়েছেন তিনি। শিশুতোষ গল্প দিয়ে সাহিত্য অঙ্গনে এ গুণী লেখকের প্রবেশ, যা প্রকাশিত হয়েছিলো ‘কিশোর বাংলা’ নামক এক পত্রিকায়। তবে পাঠকের নজরে পড়েছিলেন ‘সজনী’ নামের ছোট গল্প লিখে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (3861, 'হুমায়ূন আহমেদ', '123456', 'https://ds.rokomari.store/rokomari110/people/2222ed505a94_1.jpg', 'বাংলা সাহিত্যের এক কিংবদন্তী হুমায়ূন আহমেদ। বিংশ শতাব্দীর বাঙালি লেখকদের মধ্যে তিনি অন্যতম স্থান দখল করে আছেন। একাধারে ঔপন্যাসিক, ছোটগল্পকার ও নাট্যকার এ মানুষটিকে বলা হয় বাংলা সায়েন্স ফিকশনের পথিকৃৎ। নাটক ও চলচ্চিত্র পরিচালক হিসেবেও তিনি বেশ সমাদৃত। ১৯৪৮ সালের ১৩ই নভেম্বর, তৎকালীন পূর্ব পাকিস্তানে, নেত্রকোনা জেলার কেন্দুয়া উপজেলায় কুতুবপুরে পীরবংশে জন্মগ্রহণ করেন হুমায়ূন আহমেদ।স্বাধীনতা পরবর্তী বাঙালি জাতিকে হুমায়ুন আহমেদ উপহার দিয়েছেন তাঁর অসামান্য বই, নাটক এবং চলচ্চিত্র। চলচ্চিত্রের বদৌলতে মানুষকে করেছেন হলমুখী, তৈরি করে গেছেন বিশাল পাঠকশ্রেণীও। তাঁর নির্মিত প্রথম চলচ্চিত্র ‘আগুনের পরশমনি’ দেখতে দর্শকের ঢল নামে। কোলন ক্যান্সারে আক্রান্ত হয়ে দীর্ঘদিন চিকিৎসাধীন থাকার পর নিউ ইয়র্কের ম্যানহাটনের বেলভ্যু হাসপাতালে তিনি ইহলোক ত্যাগ করেন। গাজীপুরে তাঁর প্রিয় নুহাশ-পল্লীতে তাঁকে সমাহিত করা হয়।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (3863, 'সৈয়দ শামসুল হক', '123456', 'https://ds.rokomari.store/rokomari110/people/6af3aaaebd14_7054.jpg', 'সব্যসাচী লেখক'' হিসেবে খ্যাত সৈয়দ শামসুল হক বাংলাদেশের প্রখ্যাত একজন সাহিত্যিক, যিনি একাধারে কবিতা, ছোটগল্প, নাটক, উপন্যাস, কাব্যনাট্য, প্রবন্ধসহ সাহিত্যের বিভিন্ন শাখাতেই স্বচ্ছন্দে বিচরণ করেছেন। ৬২ বছরের দীর্ঘ লেখক-জীবনে তিনি সাহিত্যকর্ম ছাড়াও চলচ্চিত্রের চিত্রনাট্য লেখার কাজও করেছেন। খ্যাতিমান এই লেখক ১৯৩৫ সালের ২৭ ডিসেম্বর কুড়িগ্রাম জেলায় জন্মগ্রহণ করেন। আট ভাই-বোনের মাঝে তিনি ছিলেন সবচেয়ে বড়। কৃতিত্বের সাথে তিনি ম্যাট্রিক পাশ করলেও পরের বছরই বম্বে পালিয়ে গিয়ে সেখানে একটি সিনেমা প্রোডাকশন হাউজে কাজে যোগদান করেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (3864, 'মাহবুব আলম', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', '১৯৫০ সালে রংপুর শহরের এক সম্ভ্রান্ত পরিবারে জন্ম গ্রহণ করেন।শিক্ষা জীবনের শুরু রংপুর আদর্শ বিদ্যালয় এবং কারমাইকেল কলেজে।তিনি ঢাকা বিশ্ববিদ্যালয় থেকে লাইব্রেরি এন্ড ইনফরমেশন সাইন্স এ মাস্টার্স শেষ করেন।১৯৭৭ সালে তিনি পোলান্ডের ওয়ার্শ বিশ্ববিদ্যালয় থেকে পাবলিক এডমিনিস্ট্রেশনের এন্ড হাইয়ার ম্যানেজমেন্ট এর উপরে এম এ ডিগ্রি অর্জন করেন।তিনি ঢাকা বিশ্ববিদ্যালয়ের ছাত্র থাকাকালীন সময়ে মহান মুক্তিযুদ্ধ শুরু হয়।');
