INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4198, 'সুনীল গঙ্গোপাধ্যায়', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'বিশ শতকের শেষাংশে জন্ম নেওয়া সব্যসাচী একজন বাঙ্গালি সাহিত্যিক সুনীল গঙ্গোপাধ্যায়। একাধারে কবি, ঔপন্যাসিক, ছোটগল্পকার, সম্পাদক, সাংবাদিক ও কলামিস্ট- এমন বহু পরিচয়ে সাহিত্যের অগণিত ক্ষেত্রে তিনি রেখেছেন তাঁর সুকুমার ছাপ। নীললোহিত, সনাতন পাঠক কিংবা কখনো নীল উপাধ্যায় ছদ্মনামে প্রকাশিত হয়েছে সুনীল গঙ্গোপাধ্যায় এর বই সমূহ। অধুনা বাংলাদেশের মাদারীপুর জেলায় জন্মগ্রহণ করেছিলেন ১৯৩৪ সালের ৩রা সেপ্টেম্বর। কিন্তু মাত্র চার বছর বয়সেই স্কুল শিক্ষক বাবার হাত ধরে সপরিবারে পাড়ি দিয়েছিলেন কলকাতায়।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4203, 'মোস্তফা কামাল', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'জন্ম বরিশালের আন্ধার মানিক গ্রামে। সেখানেই কাটে শৈশব ও কৈশোর। উচ্চশিক্ষা ইংরেজি সাহিত্য ও রাষ্ট্রবিজ্ঞানে। দেশের অন্যতম জনপ্রিয় এই সাহিত্যিক-সাংবাদিক আড়াই দশকের বেশি সময় ধরে নিয়মিত লিখছেন। সাহিত্যের প্রায় সব শাখাতেই রয়েছে তার অবাধ বিচরণ।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4206, 'আবদুল্লাহ আবু সায়ীদ', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'বিশিষ্ট শিক্ষাবিদ ও গণমাধ্যম ব্যক্তিত্ব আবদুল্লাহ আবু সায়ীদ একইসাথে একজন খ্যাতিমান সাহিত্যিকও। আর সমাজ সংস্কারের বিভিন্ন বিষয়ের সাথে জড়িয়ে থাকায় একজন সমাজ সংস্কারক হিসেবেও পরিচয় লাভ করেছেন তিনি। এই বিশিষ্ট ব্যক্তিত্ব কলকাতার পার্ক সার্কাসে ১৯৩৯ সালে জন্মগ্রহণ করেন, তবে তাঁর পৈতৃক নিবাস বাগেরহাট জেলার কামারগাতি গ্রাম। পাবনা জিলা স্কুল থেকে তিনি মাধ্যমিক এবং বাগেরহাটের প্রফুল্লচন্দ্র কলেজ থেকে উচ্চ মাধ্যমিক পাশ করেন। এরপর উচ্চশিক্ষার্থে তিনি ঢাকা বিশ্ববিদ্যালয়ের বাংলা বিভাগে ভর্তি হন এবং পরবর্তীতে এখান থেকেই স্নাতক ও স্নাতকোত্তর ডিগ্রি অর্জন করেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4131, 'আনোয়ারা সৈয়দ হক', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'আনােয়ারা সৈয়দ হক মাত্র বারাে বছর বয়সে হাতে তুলে নিয়েছিলেন কলম। সেই থেকে কোনােদিন তার লেখা বন্ধ হয় নি। মুক্তিযুদ্ধের। সময় যখন তার মুখ ছিল বন্ধ এবং তিনি। পাকিস্তানি মেডিক্যাল কোরে মেডিক্যাল অফিসার হিসেবে কর্মরত ছিলেন, তখনাে তিনি লিখে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4138, 'হাসান হাফিজ', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'হাসান হাফিজের জন্ম নারায়ণগঞ্জের সােনারগাঁও উপজেলার এলাহি নগর গ্রামে, ১৫ অক্টোবর ১৯৫৫। যখন ছিলেন স্কুলছাত্র, প্রথম ছড়া ছাপা হয় ইত্তেফাকের কচি কাঁচার আসরে। পড়াশােনা করেছেন হােসেনপুর হাই স্কুল, ঢাকা কলেজ ও ঢাকা বিশ্ববিদ্যালয়ে। বাংলা ভাষা ও সাহিত্য,গণযােগাযােগ ও সাংবাদিকতায় এম.এ.(ডাবল)। দীর্ঘ ৪০ বছর ধরে সাংবাদিকতা পেশায় যুক্ত।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4294, 'সুকুমার রায়', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'উপেন্দ্ৰকিশোর রায়ের জ্যেষ্ঠ পুত্ৰ সুকুমারের জন্ম ১৮৮৭ খ্রিস্টাব্দে। ১৯০৬-এ পদার্থবিদ্যা ও রসায়ন দুই বিষয়েই অনার্স নিয়ে বি.এসসি পাশ করার পর ১৯১১-য় কলকাতা বিশ্ববিদ্যালয় থেকে গুরুপ্ৰসন্ন ঘোষ বৃত্তি লাভ করে মুদ্রণ বিষয়ে উচ্চ শিক্ষা লাভের জন্য তিনি বিলেতে যান। লন্ডনে ও ম্যাঞ্চেস্টারে অধ্যয়ন করেন তিনি ও তাঁর গবেষণার জন্য সম্মানিত হন। ১৯১৩-য় উপেন্দ্ৰকিশোরের সম্পাদনায় ছোটদের সচিত্ৰ মাসিক পত্রিকা সন্দেশ’’ প্রকাশিত হয়। সুকুমার দেশে ফেরারী কিছুকাল পরে ১৯১৫-য় উপেন্দ্রকিশোরের মৃত্যু হয়।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4140, 'আলী ইমাম', '123456', 'https://ds.rokomari.store/rokomari110/people/fb6c4210c5c4_124.JPG', '(জন্ম: ১৯৫০ খ্রিস্টাব্দের ৩১ ডিসেম্বর) আলী ইমামের জন্ম ব্রাহ্মণবাড়িয়ায়। জন্মের ৬ মাস পরই পুরো পরিবারসহ ঢাকায় প্রত্যাবর্তন করেন। পুরো পরিবারসহ থাকতেন পুরান ঢাকার ঠাটারীবাজারে।বাংলাদেশী শিশু সাহিত্যিক এবং অডিও ভিজ্যুয়াল ব্যবস্থাপক। আলী ইমাম বাংলাদেশ টেলিভিশনের মহা-ব্যবস্থাপক ছিলেন এবং ২০০৬ সালে চাকুরী থেকে অবসরগ্রহণ করেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4148, 'মৃত্যুঞ্জয় রায়', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'Mrityunjoy Roy- কৃষিবিদ। বাড়ি খুলনায়। ১৯৮৭ সালে বাংলাদেশ কৃষি বিশ্ববিদ্যালয়, ময়মনসিংহ থেকে উদ্যানতত্ত্ব অর্থাৎ ফুল-ফল-সবজি বিষয়ে এমএসসি ডিগ্রি অর্জন করেন। পটুয়াখালী বিজ্ঞান ও প্রযুক্তি বিশ্ববিদ্যালয়ে শিক্ষকতা দিয়ে কর্মজীবন শুরু। পরে ১৯৮৯ সালে বিসিএস (কৃষি) ক্যাডার সার্ভিসে যোগদান করেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4154, 'শামসুর রাহমান', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'নরসিংদী জেলার রায়পুরা থানা থেকে আরেকটু ভেতরে মেঘনাপাড়ের গ্রাম পাড়াতলী। কবি শামসুর রাহমানের পৈতৃক নিবাস। তবে জন্মেছিলেন ঢাকা শহরের ৪৬ নম্বর মাহুতটুলির বাড়িতে। তারিখ ১৯২৯ সালের ২৩ অক্টোবর। দশ ভাইবোনের মধ্যে জেষ্ঠ্য তিনি।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4230, 'ডা. মিজানুর রহমান কল্লোল', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'জন্ম ১ জানুয়ারি, খুলনা । এমবিবিএস পাশ করেছেন শের-ই- বাংলা মেডিক্যাল কলেজ, বরিশাল থেকে । পোস্ট গ্রাজুয়েশন করেছেন অর্থোপেডিক সার্জারিতে স্যার সলিমুল্লাহ মেডিক্যাল কলেজ, ঢাকা থেকে। লেখালেখির শুরুটা কবিতা দিয়ে হলেও সাহিত্যের বিভিন্ন শাখায় তার অবাধ বিচরণ। সায়েন্স ফিকশন ও ভৌতিক গল্প লিখেছেন প্রচুর।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4158, 'আনিসুল হক', '123456', 'https://ds.rokomari.store/rokomari110/people/6dcf2594df14_50.jpg', 'আনিসুল হক, বাংলাদেশে গত শতাব্দীর আশির দশকে আবির্ভূত হওয়া একজন প্রখ্যাত কবি, কথাসাহিত্যিক, নাট্যকার ও সাংবাদিক। কবিতা, গল্প, উপন্যাস, গদ্যকার্টুন, রম্যরচনা, ভ্রমণকাহিনী, শিশুসাহিত্যসহ সাহিত্যের নানা ক্ষেত্রে রয়েছে তার সাবলীল বিচরণ। বর্তমানে বাংলাদেশের জনপ্রিয় দৈনিক প্রথম আলোর সহযোগী সম্পাদক এবং কিশোর আলোর সম্পাদক হিসেবে কর্মরত আছেন। আনিসুল হকের জন্ম ১৯৬৫ সালের ৪ মার্চ নীলফামারীতে। শিশু মনোবিজ্ঞানের শিক্ষক বাবার অনুপ্রেরণায় ছোটবেলা থেকেই আগ্রহ জন্মেছিলো লেখালেখি আর ছবি আঁকায়।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4159, 'সুমন্ত আসলাম', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'বর্তমান সময়ের তরুণ বাংলাদেশী লেখকদের তালিকা তৈরি করতে গেলে অনায়েসেই প্রথম সারিতে জায়গা করে নেবেন কথাসাহিত্যিক সুমন্ত আসলাম। তাঁর জন্ম সিরাজগঞ্জ জেলায়, মা রওশনারা পারুল ও বাবা মরহুম সোহরাব আলী তালুকদার। স্ত্রী ফারজানা ঊর্মি আর মেয়ে সুমর্মীকে নিয়ে গড়ে উঠেছে এই লেখকের সংসার। সিরাজগঞ্জে বাড়ির পারিবারিক লাইব্রেরিতেই বই পড়ার হাতেখড়ি তার। সেই সূত্রে ছোটবেলা থেকেই বই পড়ার অভ্যাস গড়ে উঠলেও লেখালেখির শুরু ঢাকায় আসার পরে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4231, 'আহসান হাবীব', '123456', 'https://ds.rokomari.store/rokomari110/people/3195a6b53374_127.jpg', 'ড্যাড অফ বাংলাদেশী কার্টুন’ বা ‘গ্র্যান্ডফাদার অফ জোকস’সহ আরো নানা উপাধিতে ভূষিত জনপ্রিয় কার্টুনিস্ট আহসান হাবীব এর জন্ম ১৯৫৭ খ্রিস্টাব্দের ১৫ নভেম্বর সিলেট অঞ্চলে। মা আয়েশা ফয়েজ ছিলেন একজন গৃহিণী ও বাবা ফয়জুর রহমান আহমদ একজন প্রাক্তন পুলিশ কর্মকর্তা ও শহীদ মুক্তিযোদ্ধা। বাবার সরকারি চাকুরির সুবাদে বগুড়া, চট্টগ্রাম, কুমিল্লা, পিরোজপুর, দিনাজপুর, মোহনগঞ্জসহ আরও কিছু জায়গায় তাঁর শৈশব কাটে, এমনকি পড়াশোনাও করেছেন আটটি ভিন্ন স্কুলে। বাংলাদেশের প্রথম বিজ্ঞান কল্পকাহিনী ম্যাগাজিন পত্রিকা ‘মৌলিক’ ১৯৯৯ সালে আহসান হাবীব ও হাসান খুরশিদ রুমির হাত ধরেই প্রকাশিত হয়েছিল। ঢাকা বিশ্ববিদ্যালয় থেকে ভূগোলে স্নাতকোত্তর ডিগ্রী নিয়ে পাস করার পর থেকে তিন দশক ধরে তিনি ‘উন্মাদ’ পত্রিকার সাথে জড়িত আছেন, যে পত্রিকাকে তাঁর খ্যাতির মূল উৎস হিসেবে চিহ্নিত করা যায় ।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4235, 'সৈয়দ মুজতবা আলী', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'বিশিষ্ট পণ্ডিত ও রম্যরচয়িতা সৈয়দ মুজতবা আলী বাংলাদেশের একজন খ্যাতিমান সাহিত্যিক। শুধু রম্যরচনাই নয়, ছোটগল্প, উপন্যাস, অনুবাদ, ভ্রমণকাহিনী সাহিত্যের ইত্যাদি বিশেষ শাখায় রচিত সৈয়দ মুজতবা আলী এর বই সমূহ অর্জন করেছে বিশেষ খ্যাতি। বিশেষ করে তাঁর লেখা ভ্রমণকাহিনীগুলোর জুড়ি নেই, যেগুলো পাঠকদের কাছেও ব্যাপক সমাদৃত। বিখ্যাত এই সাহিত্যিক ১৯০৪ সালের ১৩ সেপ্টেম্বর সিলেটের করিমগঞ্জে জন্মগ্রহণ করেন। তাঁর পৈতৃক নিবাস সিলেটের মৌলভীবাজারে হলেও তাঁর বাবা খান বাহাদুর সৈয়দ সিকান্দার আলীর বদলির চাকরির সুবাদে শৈশব ও শিক্ষাজীবন কেটেছে ভিন্ন ভিন্ন অঞ্চলের ভিন্ন ভিন্ন স্কুলে৷ স্কুল-কলেজের পাট চুকিয়ে তিনি ১৯২১ সালে ভর্তি হন বিখ্যাত শিক্ষাপ্রতিষ্ঠান বিশ্বভারতী-তে, যেখানে তিনি অসাধারণ মেধার পরিচয় দেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4317, 'Harounuzzaman', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'Haroonuzzaman, a translator, novelist, poet and essayist, was born in Dhaka on January 13, 1951. He has been into teaching at home and abroad (Libya, Qatar) for about 27 years. For the past 12 years, he has been teaching English at Independent University, Bangladesh (IUB). Besides, he had been a feature writer of Depthnews, Manila and into English language journalism at The Independent and UNB and into broadcast journalism in Radio Pakistan, Dacca and Bangladesh Betar, Dhaka for about six years. He was the only Asian who worked as an English news broadcaster under Qatar Broadcasting Service in 1992. His notable publications include: Lalon , Radharomon Hason Raja, The Treasure-trove of Children Stories (translations), The Distant Shore, Inseparable and Juddoh (novellas). হারুনুজ্জামান, জন্ম ১৩ই জানুয়ারি, ১৯৫১ সালে ঢাকায়। পেশা শিক্ষকতা। বর্তমান কর্মস্থল-ইংরেজি বিভাগ, ইনডিপেনডেন্ট ইউনিভার্সিটি বাংলাদেশ। পূর্বাপর কর্মকাণ্ড- শিক্ষকতা (লিবিয়া, কাতার); সাংবাদিকতা (দি ইনডিপেনডেন্ট, ইউএনবি); ব্');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4161, 'সাহাদত হোসেন খান', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'প্রখ্যাত লেখক এবং সাংবাদিক সাহাদত হোসেন খান বাংলাদেশের মিডিয়াপাড়ার একজন পরিচিত মুখ। তিনি দীর্ঘকাল ধরে সাংবাদিকতা করে চলেছেন। ১৯৮৭ সালে দৈনিক দিনকাল দিয়ে সাংবাদিকতা পেশা শুরু করেন তিনি। এরপর দৈনিক ইনকিলাব, দৈনিক ইত্তেফাকসহ একাধিক জাতীয় পত্রিকায় কাজ করেছেন তিনি। ঢাকা সাংবাদিক ইউনিয়নের বিভিন্ন পদে তিনি চারবার দায়িত্বপ্রাপ্ত হন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4237, 'তারাশঙ্কর বন্দ্যোপাধ্যায়', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'তারাশঙ্কর বন্দোপাধ্যায় বিংশ শতাব্দীর একজন বিশিষ্ট কথাসাহিত্যিক ছিলেন। ১৮৯৮ সালের ২৪ জুলাই ভারতের পশ্চিমবঙ্গের বীরভূম জেলায় তাঁর জন্ম। তাঁর শৈশব কাটে এই বীরভূম জেলারই লাভপুর গ্রামে। ধর্মপরায়ণ ও আদর্শনিষ্ঠ বাবা-মায়ের কাছে তিনিও একই সততা ও আদর্শের শিক্ষা নিয়ে বেড়ে উঠতে থাকেন। লাভপুরের যাদবলাল হাই স্কুল থেকে প্রবেশিকা পরীক্ষায় পাশ করে তিনি সেন্ট জেভিয়ার্স কলেজে ভর্তি হন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4240, 'বিভূতিভূষণ বন্দ্যোপাধ্যায়', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'বাংলা সাহিত্যের অন্যতম পথিকৃৎ বিভূতিভূষণ বন্দ্যোপাধ্যায় বেশ কিছু কালজয়ী উপন্যাস রচনার মাধ্যমে জয় করে নিয়েছেন বাংলা ভাষাভাষী পাঠকদের হৃদয়। শুধু উপন্যাসই নয়, এর পাশাপাশি তিনি রচনা করেছেন বিভিন্ন ছোটগল্প, ভ্রমণকাহিনী, দিনলিপি ইত্যাদি। প্রখ্যাত এই সাহিত্যিক ১৮৯৪ সালের ১২ সেপ্টেম্বর ভারতের পশ্চিমবঙ্গের চব্বিশ পরগণা জেলায় জন্মগ্রহণ করেন, তবে তাঁর পৈতৃক নিবাস ছিল যশোর জেলায়। অত্যন্ত মেধাবী ছাত্র হিসেবে তিনি শিক্ষাজীবন অতিবাহিত করেন, যার প্রমাণ পাওয়া যায় তাঁর প্রথম বিভাগে এনট্রান্স ও আইএ পাশ করার মাধ্যমে। এমনকি তিনি কলকাতার রিপন কলেজ থেকে ডিস্টিংশনসহ বিএ পাশ করেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4166, 'হাসান আজিজুল হক', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'সাম্প্রতিক বাংলা সাহিত্যের গৌরবময় একটি নাম হাসান আজিজুল হক। ১৯৩৯ সালের ২ ফেব্রুয়ারি বর্ধমান জেলার যবগ্রামে তার জন্ম । নিজের গ্রাম থেকে স্কুলের পড়া সাঙ্গ করে ওপার-বাংলায় চলে যান। তিনি, দর্শনশাস্ত্রের পড়াশোনার পর অধ্যাপনা করেন সেখানকার কয়েকটি কলেজে। ১৯৭৩ সাল থেকে রাজশাহী বিশ্ববিদ্যালয়ে দর্শনের অধ্যাপক, এখন অবসরপ্রাপ্ত।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4242, 'রকিব হাসান', '123456', 'https://ds.rokomari.store/rokomari110/people/c384175d88e4_4378.jpg', 'রকিব হাসানের জন্ম ১৯৫০ সালে, কুমিল্লা জেলায়। আদমজী ক্যান্টনমেন্ট কলেজ থেকে বিএসসি ডিগ্রি নিলেও নিজের ধ্যান-জ্ঞান বানিয়েছেন লেখালেখিকেই। তর্কসাপেক্ষে বাংলাদেশের সবচেয়ে জনপ্রিয় গোয়েন্দা সিরিজ ‘তিন গোয়েন্দা’ এর লেখক হিসেবে আত্মপ্রকাশের আগে তিনি অন্যান্য কাজে যুক্ত ছিলেন। জনপ্রিয় মাসিক ‘রহস্যপত্রিকা’ এর সহ-সম্পাদক হিসেবে দায়িত্ব পালন করেছেন এই কৃতি লেখক। রকিব হাসান এর বই প্রথম প্রকাশিত হয় ১৯৯৭ সালে, যদিও তা ছিল ছদ্মনামে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4171, 'হুমায়ুন আজাদ', '123456', 'https://ds.rokomari.store/rokomari110/people/ee4e15091a54_975.jpg', 'প্রচলিত ধ্যানধারণার বাইরে গিয়ে ব্যক্তিগত অভীষ্ট এবং রাজনৈতিক ও ধর্মীয় বিশ্বাস দ্বারা প্রভাবিত হওয়ার মাধ্যমে ধর্ম, মৌলবাদ, প্রতিষ্ঠান ও সংস্কারের বিরুদ্ধে কলম তুলে নিয়ে বিশেষভাবে আলোচিত-সমালোচিত হয়েছিলেন প্রখ্যাত সাহিত্যিক হুমায়ুন আজাদ। প্রথাবিরোধী এবং বহুমাত্রিক এই লেখক একাধারে ছিলেন কবি, ঔপন্যাসিক, সমালোচক, গবেষক, রাজনৈতিক ভাষ্যকার, ভাষাবিজ্ঞানী এবং অধ্যাপক। বাবা-মায়ের বড় সন্তান হুমায়ুন আজাদ ১৯৪৭ সালের ২৮ এপ্রিল মুন্সিগঞ্জ জেলার শ্রীনগর উপজেলায় জন্মগ্রহণ করেন। তাঁর শৈশব ও কৈশোর কাটে রাঢ়িখাল গ্রামে, যার কথা পরবর্তীতে তাঁর বিভিন্ন সাহিত্যকর্মে উঠে এসেছে। ম্যাট্রিকুলেশন ও উচ্চ মাধ্যমিক পাশ করে তিনি ঢাকা বিশ্ববিদ্যালয়ের বাংলা বিভাগে ভর্তি হন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4172, 'শরৎচন্দ্র চট্টোপাধ্যায়', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'বাঙালির জীবনের আনন্দ-বেদনাকে সাবলীল স্বচ্ছন্দ ভাষায় যে কথাশিল্পী পরম সহানুভূতি ভরে তুলে ধরেছেন বাংলা সাহিত্যে, তিনি শরৎচন্দ্র চট্টোপাধ্যায়। ১৮৭৫ সালের ১৫ সেপ্টেম্বর, হুগলি জেলার ছোট্ট গ্রাম দেবানন্দপুরে এক দরিদ্র ব্রাহ্মণ পরিবারে জন্মগ্রহণ করেন শরৎচন্দ্র। দারিদ্র্যের কারণে তাঁর শৈশবকাল বলতে গেলে মাতুলালয় ভাগলপুরেই কেটেছে। দারিদ্র্যের কারণে ফি দিতে না পেরে বেশ কয়েকবার স্কুল বদলিও করতে হয়েছিলো ছোটবেলা থেকেই দুরন্ত ও মেধাবী শরৎচন্দ্রের। এন্ট্রান্স পাস করে কলেজে ভর্তি হলেও এফএ পরীক্ষার ফি জোগাড় করতে না পেরে পরীক্ষায় বসতে পারেননি।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4173, 'জাহানারা ইমাম', '123456', 'https://ds.rokomari.store/rokomari110/people/eab4d433c964_195.jpg', 'শহীদ জননী হিসেবে অধিক পরিচিত জাহানারা ইমাম বাংলাদেশের স্বাধীনতার ইতিহাসে এক অনন্য নাম। মুক্তিযুদ্ধের চেতনা ধারণ ও তার বাস্তবায়ন থেকে শুরু করে একাত্তরের ঘাতক দালাল নির্মূল কমিটির আহবায়ক হিসেবে বাংলাদেশের সম্মানিত ব্যক্তিবর্গের মাঝে তিনি অন্যতম। জাহানারা ইমামের জন্ম অবিভক্ত বাংলার মুর্শিদাবাদ জেলায়, ১৯২৯ সালের ৩ মে। রক্ষণশীল পরিবারে জন্ম হলেও ডেপুটি ম্যাজিস্ট্রেট পিতা আবদুল আলী তাকে পড়ালেখা করান। পরবর্তীতে পুরকৌশলী স্বামী শরীফ ইমামও তাঁর পড়ালেখায় উৎসাহ যোগান।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4174, 'রাজিব আহমেদ', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'জীবন ঘনিষ্ঠ বহুমাত্রিক লেখক রাজিব আহমেদ বাংলা মননশীল সাহিত্যকে ক্রমেই সমৃদ্ধ করে চলেছেন। তিনি বাংলাদেশের অন্যতম বিক্রয় ব্যবস্থাপনা বিশেষজ্ঞ। মোট প্রকাশিত বইয়ের সংখ্যা ৫০ অতিক্রম করে গেছে। বাংলাদেশে আত্মোন্নয়নমূলক অভিনন্দন লাভ করেছেন। রাজিব আহমেদ চারটি স্বনামধন্য বহুজাতিক কোম্পানিতে (রেকিট বেনকিজার, ইউনিলিভার, বাংলালিংক ও রবি) দশ বছরের কর্মজীবন থেকে স্বেচ্ছায় ছুটি নিয়ে এখন মুক্ত বিক্রয় প্রশিক্ষক ও অনুপ্রেরণামূলক বক্তা; জয় করে চলেছেন অসংখ্য বিক্রয় পেশাজীবী, শিক্ষার্থী ও দর্শক শ্রোতার হৃদয়।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4175, 'আহমদ শরীফ', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'বাংলাদেশের মুক্তচিন্তার অনন্য ব্যক্তিত্ব এবং মধ্যযুগের সাহিত্য ও ইতিহাসের বিদগ্ধ পণ্ডিত ড. আহমদ শরীফ পঞ্চাশ দশক থেকে নিয়মিতভাবে প্ৰবন্ধ লেখা শুরু করেছিলেন এবং তা তাঁর মৃত্যুর আগ পর্যন্ত চলমান ছিল। ড. আহমদ শরীয় এমন একজন ব্যক্তিত্ব যাকে উপেক্ষা করা যায় তবে কোনো অবস্থাতেই তাঁর বিশাল কীর্তি অস্বীকার করা যায় না । নিজস্ব দর্শন চিন্তা ও বৈশিষ্ট্যের কারণে বোদ্ধা সমাজের কাছে তিনি ছিলেন বহুল আলোচিত, সমালোচিত ও বিতর্কিত এবং তাঁর মৃত্যুর পরেও এ ধারা বহমান। ভাববাদ মানবতাবাদ ও মার্কসবাদের যৌগিক ধ্যান-ধারণা, আচার-আচরণে, বক্তব্য ও লেখনীতে। মধ্যযুগের বাংলা সাহিত্য ও সমাজ সম্পর্কে পাহাড়সম গবেষণাকর্ম, সহস্রাধিক প্ৰবন্ধ তাকে কিংবদন্তী পণ্ডিত হিশেবে উভয় বঙ্গে ব্যাপকভাবে পরিচিতি দিয়েছে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4250, 'আমীরুল ইসলাম', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'জন্ম ৭ই এপ্রিল ১৯৬৪, লালবাগ, ঢাকা । পিতা প্ৰয়াত সাইফুর রহমান। মাতা প্ৰয়াত আনজিরা খাতুন। পিতৃব্য প্রয়াত কবি হাবীবুর রহমান, খ্যাতনামা শিশুসাহিত্যিক । শিশুসাহিত্যের সকল শাখায় সমান স্বচ্ছন্দ।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4255, 'তামিম শাহরিয়ার সুবিন', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', '১৯৮২ সালের ৭ নভেম্বর ময়মনসিংহে জন্ম নেওয়া তামিম শাহরিয়ার সুবিন পেশায় একজন সফটওয়্যার ইঞ্জিনিয়ার। তার শিক্ষাজীবন শুরু হয় হোমনা সরকারি প্রাথমিক বিদ্যালয়ে। অতঃপর এ কে উচ্চ বিদ্যালয় ও নটরডেম কলেজে পড়া শেষে শাহজালাল বিজ্ঞান ও প্রযুক্তি বিশ্ববিদ্যালয়ে কম্পিউটার সায়েন্স এন্ড ইঞ্জিনিয়ারিং বিষয়ে পড়াশোনে শেষ করেন। সরকারি কর্মকর্তার ঘরে জন্ম নেওয়া সুবিনের প্রধান আকর্ষণ প্রোগ্রামিংকে ঘিরে। তিনি প্রোগ্রামিং বিষয়ক প্রায় পাঁচশোটি সমস্যা বিশ্ববিদ্যালয়ে থাকাকালে সমাধান করেছেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4333, 'সত্যজিৎ রায়', '123456', 'https://ds.rokomari.store/rokomari110/people/5e9c10414f94_2444.jpg', 'সত্যজিৎ রায় এক বাঙালি কিংবদন্তী, যিনি বিংশ শতাব্দীর অন্যতম শ্রেষ্ঠ চলচ্চিত্রকারের খ্যাতি অর্জন করেছিলেন বিশ্বদরবারে। কর্মজীবনে একইসাথে চিত্রনাট্য রচনা, সঙ্গীত স্বরলিপি রচনা, সম্পাদনা, প্রকাশক, চিত্রকর, গ্রাফিক নকশাবিদ, লেখক ও চলচ্চিত্র সমালোচক হিসেবে ভূমিকা রেখেছেন অসম্ভব গুণী এই মানুষটি। ১৯২১ সালে কলকাতার শিল্প-সাহিত্যচর্চায় খ্যাতনামা এক বাঙালি পরিবারে জন্মগ্রহণ করেন তিনি। বাংলাদেশের কিশোরগঞ্জ জেলার কটিয়াদী উপজেলার মসূয়া গ্রামে রয়েছে তাঁর পৈত্রিক ভিটা। ইতালীয় নব্য বাস্তবতাবাদী ছবি ‘লাদ্রি দি বিচিক্লেত্তে’ বা ‘দ্য বাইসাইকেল থিফ’ তাঁকে এতটাই প্রভাবিত করেছিলো যে, সিদ্ধান্ত নিয়ে ফেলেন চলচ্চিত্র নির্মাণের।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4178, 'আহমদ ছফা', '123456', 'https://ds.rokomari.store/rokomari110/people/b60f5c1bd_2254.jpg', 'বাঙালি মুসলিম লেখকদের মধ্যে অন্যতম কীর্তিমান কথাসাহিত্যিক আহমদ ছফা একাধারে ছিলেন কবি, ঔপন্যাসিক, সাংবাদিক, গণবুদ্ধিজীবী ও চিন্তাবিদ। বাবা-মায়ের দ্বিতীয় সন্তান আহমদ ছফা জন্মগ্রহণ করেন ১৯৪৩ সালের ৩০ জুন, চট্টগ্রামের চন্দনাইশ উপজেলার গাছবাড়িয়া গ্রামে। নিজ এলাকায় তাঁর শিক্ষাজীবন শুরু হয়, এবং ১৯৫৭ সালে তিনি ম্যাট্রিকুলেশন পাশ করেন। ছাত্রাবস্থায় তিনি কমিউনিস্ট পার্টির সাথে যুক্ত হন এবং মাস্টারদা সূর্যসেনের আদর্শে অনুপ্রাণিত ছিলেন। পরবর্তীতে ঢাকা বিশ্ববিদ্যালয়ে বাংলা বিভাগে ভর্তি হলেও সেখানে পড়ালেখা শেষ করেননি, এবং জাতীয় অধ্যাপক আব্দুর রাজ্জাকের অধীনে পিএইচডি শুরু করলেও তা আর শেষ করা হয়ে ওঠেনি।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4256, 'মোশতাক আহমেদ', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'লেখক মোশতাক আহমেদ এর জন্ম ১৯৭৫ সালের ৩০ ডিসেম্বর, ফরিদপুর জেলায়। পেশায় একজন সহকারী পুলিশ সুপার হওয়া সত্ত্বেও লেখালেখির প্রতি তাঁর আগ্রহ প্রচুর। এ পর্যন্ত সায়েন্স ফিকশন নিয়েই সবচেয়ে বেশি লিখেছেন। বাংলা সায়েন্স ফিকশন জগতে তার পোক্ত একটি অবস্থান সৃষ্টি হয়েছে। এছাড়াও মোশতাক আহমেদ এর বই সমূহ ভৌতিক, প্যারাসাইকোলজি, মুক্তিযুদ্ধ, কিশোর ক্ল্যাসিক, ভ্রমণ ইত্যাদি জঁনরাতে বিভক্ত।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4339, 'রবীন্দ্রনাথ ঠাকুর', '123456', 'https://ds.rokomari.store/rokomari110/people/068d7a1a0_930.jpg', 'বিশ্বকবি রবীন্দ্রনাথ ঠাকুর ছিলেন একাধারে ঔপন্যাসিক, নাট্যকার, প্রাবন্ধিক, ছোটগল্পকার, চিত্রশিল্পী, সংগীতস্রষ্টা, অভিনেতা, কন্ঠশিল্পী, কবি, সমাজ-সংস্কারক এবং দার্শনিক। গীতাঞ্জলি কাব্যগ্রন্থের ইংরেজি অনুবাদের জন্য প্রথম বাঙালি হিসেবে ১৯১৩ সালে তিনি সাহিত্যে নোবেল পুরষ্কার লাভ করেন। রবীন্দ্রনাথ ঠাকুর ১৮৬১ সালের ৭ মে তৎকালীন ব্রিটিশ-শাসিত ভারতে কলকাতার ধনাঢ্য ও সংস্কৃতিমনা জোড়াসাঁকোর ঠাকুরবাড়িতে জন্মগ্রহণ করেন। শৈশব থেকেই তিনি লেখালেখিতে মনোনিবেশ করেন। ভানুসিংহ ঠাকুর ছিল তাঁর ছদ্মনাম।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4341, 'শাহরিয়ার কবির', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'শাহরিয়ার কবির বাংলাদেশের একজন খ্যাতিনামা লেখক, সাংবাদিক, ডকুমেন্টরী চলচ্চিত্র নির্মাতা। ১৯৫০ খ্রীস্টাব্দের ২০ নভেম্বর তিনি ফেনীতে জন্মগ্রহণ করেন। লেখক হিসাবে তার প্রধান পরিচয় তিনি একজন শিশুসাহিত্যিক। তার নির্মিত প্রামাণ্যচিত্রের মধ্যে জিহাদের প্রতিকৃতি অন্যতম।১৯৯২ সাল থেকে তিনি বাংলাদেশের স্বাধীনতা যুদ্ধের বিপক্ষ শক্তির বিরূদ্ধে কাজ করে চলেছেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4342, 'মুনতাসীর মামুন', '123456', 'https://ds.rokomari.store/rokomari110/people/2f4178e4cf34_6.jpg', 'মুনতাসীর মামুনের পৈতৃক বাড়ি চাঁদপুর জেলার গুলবাহার গ্রামে, কিন্তু তিনি ঢাকার ইসলামপুরে নানাবাড়িতে জন্মগ্রহণ করেন। তার বেড়ে ওঠা চট্টগ্রামে। সেখানেই পোর্ট ট্রাস্ট প্রাইমারি ও হাই স্কুলে প্রাথমিক শিক্ষা নেন। পরে ভর্তি হন চট্টগ্রাম কলেজে। অতঃপর ঢাকা বিশ্ববিদ্যালয়ের ইতিহাস বিভাগ থেকে স্নাতক, স্নাতকোত্তর সম্পন্ন করেন এবং একই বিভাগ থেকে পিএইচডি ডিগ্রি লাভ করেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4185, 'সিরাজুল ইসলাম চৌধুরী', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'সিরাজুল ইসলাম চৌধুরী (জন্ম. ১৯৩৬) পেশায় সাহিত্যের অধ্যাপক এবং অঙ্গীকারে লেখক। এই দুই সত্তার ভেতর হয়তো একটা দ্বন্দ্বও রয়েছে, তবে সেটা অবৈরী, মোটেই বৈরী স্বভাবের নয়। পেশাগত জীবনে তিনি ছিলেন ঢাকা বিশ্ববিদ্যালয়ের ইংরেজি সাহিত্যের অধ্যাপক, অবসরগ্রহণের পর ওই বিশ্ববিদ্যালয়েরই প্রফেসর এমেরিটাস হিসাবে মনোনীত হয়েছেন। তিনি শিক্ষা লাভ করেছেন রাজশাহী, কলকাতা, ঢাকা এবং ইংল্যান্ডের লীডস ও লেস্টার বিশ্ববিদ্যালয়ে। লেখার কাজের পাশাপাশি তিনি ‘নতুন দিগন্ত’ নামে সাহিত্য-সংস্কৃতি বিষয়ক একটি ত্রৈমাসিক পত্রিকা সম্পাদনা করছেন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4186, 'বঙ্কিমচন্দ্র চট্টোপাধ্যায়', '123456', 'https://ds.rokomari.store/rokomari110/people/b79bc8c29ac4_1224.jpg', 'Bankim Chandra Chattapadhyaya- জন্ম হয় বর্তমান উত্তর ২৪ পরগনা জেলার নৈহাটি শহরের নিকটস্থ কাঁঠালপাড়া গ্রামে। তারিখ ২৬ জুন, ১৮৩৮ অর্থাৎ ১৩ আষাঢ় ১২৪৫। চট্টোপাধ্যায়দের আদিনিবাস ছিল হুগলি জেলার দেশমুখো গ্রামে। বঙ্কিমচন্দ্রের প্রপিতামহ রামহরি চট্টোপাধ্যায় মাতামহের সম্পত্তি পেয়ে কাঁঠালপাড়ায় আসেন এবং সেখানেই বসবাস শুরু করেন। রামহরির পৌত্র যাদবচন্দ্র চট্টোপাধ্যায়ের তৃতীয় পুত্র বঙ্কিমচন্দ্র।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4188, 'দেবীপ্রসাদ চট্টোপাধ্যায়', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'Debiprasad Chattopadhyaya (জন্ম: ১৯ নভেম্বর, ১৯১৮ - মৃত্যু: ৮ মে, ১৯৯৩) ভারতের একজন প্রখ্যাত মার্কসবাদী দার্শনিক। তিনি প্রাচীন ভারতের দর্শনের বস্তুবাদকে উদ্ঘাটন করেছেন। তার সবচেয়ে বড় কাজ হল লোকায়তের প্রাচীন দর্শনকে তিনি বিরুদ্ধপক্ষের বিকৃতি হতে রক্ষা করেন এবং তা সংগ্রহ ও প্রকাশ করেছেন। এছাড়াও তিনি প্রাচীন ভারতের বিজ্ঞানের ইতিহাস ও বিজ্ঞানের পদ্ধতি সম্পর্কেও গবেষণা করেছেন বিশেষ করে প্রাচীন চিকিৎসক চরক ও শ্রুশ্রুত সম্পর্কে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4190, 'মানিক বন্দ্যোপাধ্যায়', '123456', 'https://ds.rokomari.store/rokomari110/people/d20cbd94b984_610.jpg', 'শ্রমজীবী মানুষের সংগ্রাম, মধ্যবিত্ত সমাজের কৃত্রিমতা, নিয়তিবাদ ইত্যাদি বিষয়কে লেখার মধ্যে তুলে এনে বাংলা সাহিত্যে যিনি অমর হয়েছেন, তিনি হলেন প্রখ্যাত ভারতীয় বাঙালি কথাসাহিত্যিক মানিক বন্দ্যোপাধ্যায়। খ্যাতিমান এই সাহিত্যিক ১৯০৮ সালের ১৯ মে বিহারের সাঁওতাল পরগনায় জন্মগ্রহণ করেন। তাঁর প্রকৃত নাম প্রবোধকুমার বন্দ্যোপাধ্যায়, আর মানিক ছিলো তাঁর ডাকনাম। বাবার বদলির চাকরিসূত্রে তাঁর শৈশব, কৈশোর ও ছাত্রজীবন কেটেছে বাংলাদেশ ও ভারতের বিভিন্ন অঞ্চলে, যার ফলে বিভিন্ন অঞ্চলের পটভূমিতে বিভিন্ন সাহিত্য রচনা করেছেন তিনি। প্রবেশিকা ও আইএসসি পরীক্ষায় উত্তীর্ণ হবার পর মানিক বন্দ্যোপাধ্যায় গণিত বিষয়ে অনার্স করতে কলকাতা প্রেসিডেন্সি কলেজে ভর্তি হন।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4191, 'মোহিত কামাল', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'কথাসাহিত্যিক মোহিত কামাল, মা: মাসুদা খাতুন, বাবা: আসাদুল হক, স্ত্রী: মাহফুজা আক্তার মিলি, সন্তান: মাহবুব ময়ূখ রিশাদ ও জিদনি ময়ূখ স্বচ্ছ, জন্ম: ২ জানুয়ারি ১৯৬০,সন্ধীপ চট্টগ্রাম।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4269, 'ম্যাক্সিম গোর্কি', '123456', 'https://ds.rokomari.store/rokomari110/people/e268e1c2d_3180.jpg', '(মার্চ ২৮, ১৮৬৮ – জুন ১৮, ১৯৩৬) বিখ্যাত রুশ সাহিত্যক। তিনি নিজেই তার সাহিত্যক ছদ্মনাম হেসেবে বেছে নেন ''গোর্কি'' অর্থাৎ ''তেতো'' নামকে। তার অনেক বিখ্যাত রচনার মধ্যে মা একটি কালজয়ী উপন্যাস।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4194, 'হরিশংকর জলদাস', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'প্রখ্যাত ঔপন্যাসিক হরিশংকর জলদাস ১৯৫৫ সালের ১২ই অক্টোবর বাংলাদেশের চট্টগ্রাম বিভাগের উত্তর পতেঙ্গা গ্রামের এক জেলে পরিবারে জন্মগ্রহণ করেন। তার বাবা যুধিষ্ঠির জলদাস পেশায় ছিলেন একজন জেলে। ফলে আর্থিক অভাব অনটন আর অর্থনৈতিক টানাপোড়েনই ছিল হরিশংকরের বেড়ে ওঠার সঙ্গী। হরিশংকর জলদাসের শৈশব-কৈশোর কাটে পতেঙ্গার কৈবর্তপাড়ায়। তার বাবার স্বপ্ন ছিল ছেলেকে জেলের জীবনযুদ্ধে না জড়িয়ে শিক্ষিত করবেন যেন ছেলে সম্মানের জীবনযাপন করতে পারে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4275, 'অনীশ দাস অপু', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'পাঠকনন্দিত অনুবাদক অনীশ দাস অপু ১৯৬৯ সালের ৫ ডিসেম্বর বরিশাল জেলায় জন্মগ্রহণ করেন। তাঁর পিতা প্রয়াত লক্ষ্মী কান্ত দাস। ১৯৯৫ সালে এই কৃতি লেখক ঢাকা বিশ্ববিদ্যালয়ের আন্তর্জাতিক সম্পর্ক বিভাগ থেকে সম্মানসহ স্নাতকোত্তর পাস করেন। ছাত্রাবস্থায়ই তিনি দেশের জনপ্রিয় ও শীর্ষস্থানীয় সাপ্তাহিক, পাক্ষিক ও মাসিক পত্রিকাগুলোতে অনুবাদক হিসেবে কাজ করতে শুরু করেন, এবং ফিচার, গল্প ও উপন্যাস অনুবাদ করতে থাকেন। অনীশ দাস অপু এর বইগুলো সাধারণত থ্রিলার ও হরর ধাঁচেরই হয়ে থাকে।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4276, 'মোস্তাক আহ্‌মাদ', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', 'সৈয়দ মোস্তাক আহ্‌মাদের জন্ম ১৯৫২ সালের ১ জানুয়ারি রংপুরের বদরগঞ্জ উপজেলার রহমতপুর গ্রামে। তার পিতা মাওলানা মুহাম্মদ তমিজ উদ্দীন (র.) ছিলেন একজন পীর ও আধ্যাত্মিক পুরুষ। তার মাতা মনোয়ারা বেগমও ছিলেন সম্ভ্রান্ত পরিবার থেকে আগত। এরকম সম্ভ্রান্ত পরিবারের সন্তান মোস্তাক আহ্‌মাদ এর ভাগ্য যেন নির্ধারিতই ছিল যে তিনি বড় হয়ে ধর্ম ও সুফি দর্শন নিয়ে লিখবেন। পারিবারিক ঐতিহ্যের কল্যাণে শৈশব থেকে তার ধর্মীয় শিক্ষার ভিত মজবুত হয়।');
INSERT INTO C##SQUIRREL.AUTHOR (ID, NAME, PASSWORD, IMAGE, DESCRIPTION) VALUES (4277, 'শাহরিয়ার', '123456', 'https://www.rokomari.com/static/new/img/author/author.png', '১৯৮২ সালের ৭ নভেম্বর ময়মনসিংহে জন্ম নেওয়া তামিম শাহরিয়ার সুবিন পেশায় একজন সফটওয়্যার ইঞ্জিনিয়ার। তার শিক্ষাজীবন শুরু হয় হোমনা সরকারি প্রাথমিক বিদ্যালয়ে। অতঃপর এ কে উচ্চ বিদ্যালয় ও নটরডেম কলেজে পড়া শেষে শাহজালাল বিজ্ঞান ও প্রযুক্তি বিশ্ববিদ্যালয়ে কম্পিউটার সায়েন্স এন্ড ইঞ্জিনিয়ারিং বিষয়ে পড়াশোনে শেষ করেন। সরকারি কর্মকর্তার ঘরে জন্ম নেওয়া সুবিনের প্রধান আকর্ষণ প্রোগ্রামিংকে ঘিরে। তিনি প্রোগ্রামিং বিষয়ক প্রায় পাঁচশোটি সমস্যা বিশ্ববিদ্যালয়ে থাকাকালে সমাধান করেছেন।');
