INSERT INTO C##SQUIRREL.RATES (ID, USER_ID, BOOK_ID, STARS, CREATED_AT, REVIEW) VALUES (110, 24, 251, 5, TO_DATE('2022-02-17 01:04:08', 'YYYY-MM-DD HH24:MI:SS'), 'বইটা ৭টা বইয়ের সমষ্টি আরকি। আলো নিয়ে বইটা ভালো। অনেক হেল্পফুলও। কোয়ান্টাম মেকানিক্স নিয়ে বইটি বুঝতে অনেকের সমস্যা হবে। তাছাড়া কোয়ান্টাম বইটিতে কোয়ান্টাম মেকানিক্স নেই বললেই চলে। তরঙ্গ নিয়েই বেশি কথা হয়েছে। নিউরনে অনুরণন সিরিজে প্রশ্ন নেই তবে গণিতবিদ দের কথা আছে। থিওরি অব রিলিটিভিটি বইটি ছোটদের জন্য। গণিতের মজা বইটা বেশ চমৎকার লেগেছে।');
