-- 5
create TABLE app_user (
    ID          NUMBER GENERATED BY DEFAULT AS IDENTITY ,
    Name        VARCHAR2(100) NOT NULL ,
    EMAIL       varchar2(100) not null,
    password    VARCHAR2(1024) NOT NULL ,
    address     VARCHAR2(1000),
